import en_ner_bc5cdr_md
import spacy


def extract_entities(text):
    nlp = spacy.load("en_ner_bc5cdr_md")
    doc = nlp(text)
    entities = [(ent.text, ent.label_) for ent in doc.ents]
    return entities


