def main():
    entities = extract_entities(text)

    if entities:
        print("Обнаруженные именованные сущности:")
        for entity, label in entities:
            print(f"- '{entity}' (Тип: {label})")
    else:
        print("Именованные сущности не обнаружены.")

if __name__ == "__main__":
    main()